import { Chart, registerables } from "chart.js";

// Enregistrement des composants nécessaires
Chart.register(...registerables);
export default {
  mounted() {
    this.renderChart();
  },
  methods: {
    renderChart() {
      const ctx = document.getElementById("cryptoChart").getContext("2d");

      // Création d'un gradient pour la ligne
      const gradient = ctx.createLinearGradient(0, 0, 0, 400);
      gradient.addColorStop(0, "rgba(75,192,192,0.4)");
      gradient.addColorStop(1, "rgba(75,192,192,0)");
      new Chart(ctx, {
        type: "line",
        // Type de graphique
        data: {
          labels: ["Jan", "Feb", "Mar", "Apr", "May"],
          // Labels pour l'axe des X
          datasets: [{
            label: "Prix Bitcoin",
            data: [50000, 52000, 54000, 53000, 54000],
            borderColor: "rgb(75, 192, 192)",
            // Couleur de la ligne
            backgroundColor: gradient,
            // Fond avec gradient
            pointBorderColor: "rgba(255, 255, 255, 0.8)",
            // Couleur des points
            pointBackgroundColor: "rgb(75, 192, 192)",
            // Couleur de fond des points
            pointRadius: 5,
            // Taille des points
            fill: true,
            // Remplir sous la ligne avec le gradient
            tension: 0.4 // Arrondir les angles des lignes
          }]
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              display: false // Masquer la légende pour un style épuré
            }
          },
          scales: {
            x: {
              ticks: {
                color: "#fff" // Couleur des ticks de l'axe X
              },
              grid: {
                display: false // Masquer les lignes de la grille
              }
            },
            y: {
              ticks: {
                color: "#fff",
                // Couleur des ticks de l'axe Y
                callback: function (value) {
                  return "$" + value; // Ajouter un symbole dollar
                }
              },
              grid: {
                color: "rgba(255, 255, 255, 0.1)" // Ligne de grille discrète
              }
            }
          }
        }
      });
    }
  }
};
